
'use strict';;(function($,window,document,undefined){var pluginName="istexAuth";var defaults=istexConfigDefault;function Plugin(element,options){this.elt=element;this.settings=$.extend({},defaults,istexConfig,options);this._defaults=defaults;this._name=pluginName;this.init();}
Plugin.prototype.init=function(){var self=this;self.getAuthMode(function(err,needAuth,authMode){if(needAuth=='none'){self.setupGenericRequester(authMode);self.removeConnectBtn();$.event.trigger(self.settings.connectedEventName,[self]);}else if(needAuth=='redirect'){self.authWithRedirect(function(err){if(!err){self.setupGenericRequester(authMode);self.removeConnectBtn();$.event.trigger(self.settings.connectedEventName,[self]);}});}else if(needAuth=='http'){self.authWithHTTP(function(err,options){if(!err){self.setupGenericRequester(authMode,options);self.removeConnectBtn();$.event.trigger(self.settings.connectedEventName,[self]);}});}});};Plugin.prototype.getAuthMode=function(cb){var self=this;$.ajax({url:self.settings.istexApi+'/corpus/',success:function(){return cb(null,'none','ajax');},error:function(opt,err){if(opt.status===0||opt.status===302){$.jsonp({url:self.settings.istexApi+'/corpus/',callbackParameter:"callback",success:function(){cb(null,'none','jsonp');},error:function(){cb(null,'redirect','jsonp');}});}else{return cb(null,'http','ajax');}}});};Plugin.prototype.authWithRedirect=function(cb){var self=this;self.insertConnectBtnIfNotExists(function(){window.open(self.settings.istexApi+'/ezproxy-auth-and-close.html');$(window).focus(function(){self.getAuthMode(function(err,needAuth,authMode){if(needAuth=='none'){cb(null);}else{cb(new Error("Unable to authenticate"));}});});});};Plugin.prototype.insertConnectBtnIfNotExists=function(cb){var self=this;if($(self.elt).find('.istex-ezproxy-auth-btn').length>0){return;}
var authButtonHtml=$('<button class="istex-ezproxy-auth-btn">Se connecter<div></div></button>').hide();$(self.elt).append(authButtonHtml);authButtonHtml.fadeIn();$(self.elt).find('.istex-ezproxy-auth-btn').click(cb);};Plugin.prototype.removeConnectBtn=function(cb){var self=this;$(self.elt).find('.istex-ezproxy-auth-btn').remove();};Plugin.prototype.authWithHTTP=function(cb){var self=this;self.insertConnectBtnIfNotExists(function(clickEvent){if($(self.elt).find('.istex-auth-form').length>0){return;}
var connectButton=$(clickEvent.target);connectButton.hide();var authFormHtml=$('<form class="istex-auth-form">'+'<div class="istex-auth-form-wrapper">'+'<input class="istex-auth-form-login" type="text" value="" placeholder="Votre login ..." />'+'<input class="istex-auth-form-password" type="password" value="" placeholder="Votre mot de passe ..." />'+'<input class="istex-auth-form-submit" type="submit" value="Se connecter" default="default"/>'+'<button class="istex-auth-form-cancel">Annuler</button>'+'</div>'+'<p class="istex-auth-form-info">'+'<a href="https://ia.inist.fr/people/newer" target="_blank">S\'inscrire</a> | '+'<a href="https://ia.inist.fr/auth/retrieve" target="_blank">Retrouver son mot de passe</a> | '+'<a href="mailto:istex@inist.fr?subject=Demande d\'un accès à la plateforme ISTEX">Demander une autorisation Istex</a>'+'</p>'+'<p class="istex-auth-form-error"></p>'+'</form>').hide();$(self.elt).append(authFormHtml);authFormHtml.fadeIn();$(self.elt).find('.istex-auth-form-login').focus();$(self.elt).find('.istex-auth-form-cancel').click(function(){$(self.elt).find('.istex-auth-form').remove();connectButton.fadeIn();return false;});$(self.elt).find('.istex-auth-form').submit(function(){authFormHtml.find('input').attr('disabled','disabled');$(self.elt).find('.istex-auth-form-error').hide();var httpOptions={headers:{"Authorization":"Basic "+btoa($(self.elt).find('.istex-auth-form-login').val()+":"+
$(self.elt).find('.istex-auth-form-password').val())}};$.ajax({url:self.settings.istexApi+'/corpus/',headers:httpOptions.headers,success:function(){$(self.elt).find('.istex-auth-form').fadeOut({complete:function(){cb(null,httpOptions);}});},error:function(opt,err){authFormHtml.find('input').removeAttr('disabled');$(self.elt).find('.istex-auth-form-error').text("Le nom d'utilisateur ou le mot de passe saisi est incorrect.").fadeIn();}});return false;});});};Plugin.prototype.setupGenericRequester=function(authMode,authOptions){var self=this;if(authMode=='jsonp'){self.istexApiRequester=function(options){var reqOpt=$.extend({callbackParameter:"callback"},authOptions,options);return $.jsonp(reqOpt);};}else{self.istexApiRequester=function(options){var reqOpt=$.extend(authOptions,options);return $.ajax(reqOpt);};}};$.fn[pluginName]=function(options){this.each(function(){if(!$.data(this,"plugin_"+pluginName)){$.data(this,"plugin_"+pluginName,new Plugin(this,options));}});return this;};})(jQuery,window,document);